/// <reference types="node" />
export default class FrameParser {
    static readData(b: Buffer, type: string, major: number, includeCovers: boolean): any;
    /**
     * Converts TMCL (Musician credits list) or TIPL (Involved people list)
     * @param entries
     */
    private static functionList(entries);
    /**
     * id3v2.4 defines that multiple T* values are separated by 0x00
     * id3v2.3 defines that TCOM, TEXT, TOLY, TOPE & TPE1 values are separated by /
     * @param {number} major Major version, e.g. (4) for  id3v2.4
     * @param {string} text Concatenated tag value
     * @returns {string[]} Slitted value
     */
    private static splitValue(major, text);
    private static trimArray(values);
    private static readIdentifierAndData(b, offset, length, encoding);
    private static getTextEncoding(byte);
    private static getNullTerminatorLength(enc);
}
